    private void ButtonEntrarActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_ButtonEntrarActionPerformed
       String login, senha;
       
       login = CampoLoginLogin.getText();
       senha = new String(CampoSenhaLogin.getPassword());
       
       UsuarioClass user = new UsuarioClass();
       
       boolean resp = user.consultarLogin(login, senha);
       
       if(resp == true){
           new frmPrincipalVIEW().setVisible(true);
       }else{
           JOptionPane.showMessageDialog(null, "Login ou senha inv√°lidos.");
       }
    }//GEN-LAST:event_ButtonEntrarActionPerformed

